import kivy
from kivy.uix.gridlayout import GridLayout
from kivy.app import App
from kivy.uix.label import Label
from kivy.uix.textinput import TextInput
from kivy.uix.button import Button
from kivy.uix.image import Image,AsyncImage


class LoginScreen(GridLayout):
	def __init__(self,**kwargs):
		super().__init__(**kwargs)
		self.cols=4
		self.add_widget(Label(text="UserName"))
		self.username=TextInput(multiline=False)
		self.add_widget(self.username)
		self.add_widget(Label(text="Password"))
		self.password=TextInput(password=True,multiline=False)
		self.add_widget(self.password)
		self.add_widget(Button(text="Submit",pos_hint={'center_x':0.5},on_press=self.printpress,size_hint=(0.2,0.2)))
		#self.add_widget(AsyncImage(source='data:image/jpeg;base64,/9j/4AAQSkZJRgABAQAAAQABAAD/
		#2wCEAAkGBxITEhUQERMSFhUVFRgXFxUXFxcWGBUaFRUXFhYXFxUYHSggGBolGxcVITIhJSkrLi4uFx8zODMtNygtLisBCgoKDg0OGxAQGzcmICMwLi0tKysyLjUrLzYtNys1LSsxNTAtLS0tLS0tNS03LS03LTIvLy8uLS0uLzU3LS0rK
		#'))


	def printpress(self,obj):
		print("Button Pressed")

class MyApp(App):
	def build(self):
		return LoginScreen()


MyApp().run()